import React, { useEffect, useState } from 'react';
import {
  View, Text, StyleSheet, FlatList, Image, Animated,
} from 'react-native';
import AsyncStorage from '@react-native-async-storage/async-storage';
import { AnimatedCircularProgress } from 'react-native-circular-progress';
import { MaterialCommunityIcons } from '@expo/vector-icons';

const ProgressScreen = () => {
  const [progressData, setProgressData] = useState([]);
  const fadeAnim = new Animated.Value(0);

  useEffect(() => {
    fetchProgress();
    Animated.timing(fadeAnim, {
      toValue: 1,
      duration: 1000,
      useNativeDriver: true,
    }).start();
  }, []);

  const fetchProgress = async () => {
    const steps = parseInt(await AsyncStorage.getItem('stepCount')) || 0;
    const calories = JSON.parse(await AsyncStorage.getItem('calorieIntakeHistory')) || [];
    const cycling = JSON.parse(await AsyncStorage.getItem('cyclingEntries')) || [];
    const workouts = JSON.parse(await AsyncStorage.getItem('workoutHistory')) || [];

    const totalCalories = calories.reduce((sum, e) => sum + (e.intake || 0), 0);
    const totalCycling = cycling.reduce((sum, e) => sum + (e.distance || 0), 0);
    const totalWorkoutMins = workouts.reduce((sum, e) => sum + (e.duration || 0), 0);
    const totalWorkoutCals = workouts.reduce((sum, e) => sum + (e.calories || 0), 0);

    const weeklyAvg = (val, count) => (count ? (val / count).toFixed(1) : '0');

    setProgressData([
      {
        key: 'steps',
        title: 'Steps Walked',
        value: steps,
        goal: 10000,
        unit: 'steps',
        avg: steps,
        badge: require('../assets/badges/steps.png'),
        icon: 'walk',
      },
      {
        key: 'calories',
        title: 'Calories Consumed',
        value: totalCalories,
        goal: 2000 * 7,
        unit: 'kcal',
        avg: weeklyAvg(totalCalories, calories.length),
        badge: require('../assets/badges/calories.png'),
        icon: 'fire',
      },
      {
        key: 'cycling',
        title: 'Cycling Distance',
        value: totalCycling,
        goal: 10 * 7,
        unit: 'km',
        avg: weeklyAvg(totalCycling, cycling.length),
        badge: require('../assets/badges/cycling.png'),
        icon: 'bike',
      },
      {
        key: 'workout',
        title: 'Workout Calories',
        value: totalWorkoutCals,
        goal: 1500,
        unit: 'kcal',
        avg: weeklyAvg(totalWorkoutCals, workouts.length),
        badge: require('../assets/badges/workout.png'),
        icon: 'dumbbell',
      },
    ]);
  };

  const renderItem = ({ item }) => (
    <View style={styles.card}>
      <View style={styles.badge}>
        <Image source={item.badge} style={styles.badgeImg} />
      </View>
      <View style={styles.info}>
        <Text style={styles.title}>{item.title}</Text>
        <Text style={styles.avg}>ðŸ“Š Weekly Avg: {item.avg} {item.unit}</Text>
        <AnimatedCircularProgress
          size={100}
          width={10}
          fill={(item.value / item.goal) * 100}
          tintColor="#03a9f4"
          backgroundColor="#eee"
          duration={1000}
        >
          {() => (
            <View style={{ alignItems: 'center' }}>
              <MaterialCommunityIcons name={item.icon} size={24} color="#03a9f4" />
              <Text style={styles.ringText}>
                {item.value} {item.unit}
              </Text>
            </View>
          )}
        </AnimatedCircularProgress>
      </View>
    </View>
  );

  return (
    <Animated.View style={[styles.container, { opacity: fadeAnim }]}>
      <Text style={styles.header}>ðŸ“ˆ Your Weekly Progress</Text>
      <FlatList
        data={progressData}
        keyExtractor={item => item.key}
        renderItem={renderItem}
        contentContainerStyle={{ paddingBottom: 40 }}
      />
    </Animated.View>
  );
};

export default ProgressScreen;

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#f2f9ff',
    padding: 20,
  },
  header: {
    fontSize: 22,
    fontWeight: 'bold',
    color: '#222',
    textAlign: 'center',
    marginBottom: 20,
  },
  card: {
    backgroundColor: '#fff',
    padding: 18,
    marginVertical: 10,
    borderRadius: 14,
    elevation: 3,
    flexDirection: 'row',
    alignItems: 'center',
    position: 'relative',
  },
  badge: {
    position: 'absolute',
    top: -10,
    right: -10,
    zIndex: 1,
  },
  badgeImg: {
    width: 50,
    height: 50,
  },
  info: {
    flex: 1,
    paddingLeft: 10,
    alignItems: 'center',
  },
  title: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#333',
    marginBottom: 6,
  },
  avg: {
    fontSize: 14,
    color: '#777',
    marginBottom: 6,
  },
  ringText: {
    fontSize: 16,
    fontWeight: 'bold',
    color: '#03a9f4',
  },
});
