import React, { useEffect, useState, useContext } from 'react';
import { View, Text, StyleSheet, TouchableOpacity, ScrollView, Alert } from 'react-native';
import { useNavigation } from '@react-navigation/native';
import { AnimatedCircularProgress } from 'react-native-circular-progress';
import { Ionicons, FontAwesome5, MaterialCommunityIcons } from '@expo/vector-icons';
import { UserContext } from '../context/UserContext';

export default function HomeScreen() {
  const navigation = useNavigation();
  const { username } = useContext(UserContext);
  const [currentTime, setCurrentTime] = useState('');
  const [currentDate, setCurrentDate] = useState('');

  const [stepCount, setStepCount] = useState(6200);
  const [calories, setCalories] = useState(450);
  const [cyclingDistance, setCyclingDistance] = useState(3.2); // in km

  useEffect(() => {
    const updateTime = () => {
      const now = new Date();
      setCurrentTime(now.toLocaleTimeString([], { hour: '2-digit', minute: '2-digit' }));
      setCurrentDate(now.toLocaleDateString(undefined, { day: 'numeric', month: 'long' }));
    };
    updateTime();
    const interval = setInterval(updateTime, 1000);
    return () => clearInterval(interval);
  }, []);

  const handleGoogleFitSync = () => {
    Alert.alert("Google Fit", "Data synced successfully!");
  };

  return (
    <ScrollView contentContainerStyle={styles.container}>
      <View style={styles.topRow}>
        <Text style={styles.greeting}>ðŸ‘‹ Welcome back, {username}</Text>
        <Text style={styles.time}>{currentDate} | {currentTime}</Text>
      </View>

      <View style={styles.cardsRow}>
        <TouchableOpacity style={styles.card} onPress={() => navigation.navigate('Workout')}>
          <AnimatedCircularProgress
            size={100}
            width={10}
            fill={(stepCount / 10000) * 100}
            tintColor="#4caf50"
            backgroundColor="#eee"
          >
            {() => (
              <Ionicons name="walk" size={30} color="#4caf50" />
            )}
          </AnimatedCircularProgress>
          <Text style={styles.cardLabel}>Steps</Text>
          <Text style={styles.cardValue}>{stepCount}</Text>
        </TouchableOpacity>

        <TouchableOpacity style={styles.card} onPress={() => navigation.navigate('CalorieTracker')}>
          <AnimatedCircularProgress
            size={100}
            width={10}
            fill={(calories / 2000) * 100}
            tintColor="#ff9800"
            backgroundColor="#eee"
          >
            {() => (
              <FontAwesome5 name="fire" size={30} color="#ff9800" />
            )}
          </AnimatedCircularProgress>
          <Text style={styles.cardLabel}>Calories</Text>
          <Text style={styles.cardValue}>{calories} kcal</Text>
        </TouchableOpacity>
      </View>

      <TouchableOpacity style={styles.cardFull} onPress={() => navigation.navigate('CyclingTracker')}>
        <AnimatedCircularProgress
          size={120}
          width={10}
          fill={(cyclingDistance / 10) * 100}
          tintColor="#2196f3"
          backgroundColor="#eee"
        >
          {() => (
            <MaterialCommunityIcons name="bike" size={40} color="#2196f3" />
          )}
        </AnimatedCircularProgress>
        <Text style={styles.cardLabel}>Cycling</Text>
        <Text style={styles.cardValue}>{cyclingDistance} km</Text>
      </TouchableOpacity>

      <TouchableOpacity style={styles.syncButton} onPress={handleGoogleFitSync}>
        <MaterialCommunityIcons name="google-fit" size={20} color="white" />
        <Text style={styles.syncButtonText}>Sync with Google Fit</Text>
      </TouchableOpacity>

      <Text style={styles.quote}>ðŸ’ª Letâ€™s have a fit day ahead!</Text>
    </ScrollView>
  );
}

const styles = StyleSheet.create({
  container: {
    padding: 20,
    backgroundColor: '#f8f9fa',
    alignItems: 'center',
    paddingBottom: 60
  },
  topRow: {
    width: '100%',
    marginBottom: 20,
  },
  greeting: {
    fontSize: 20,
    fontWeight: '600',
    color: '#1a1a1a',
    fontFamily: 'System', // You can replace with a custom font like 'Poppins' if added
  },
  time: {
    fontSize: 14,
    color: '#888',
    textAlign: 'right',
    fontFamily: 'System',
  },
  cardsRow: {
    flexDirection: 'row',
    justifyContent: 'space-between',
    width: '100%',
    gap: 10,
  },
  card: {
    backgroundColor: '#fff',
    padding: 16,
    borderRadius: 16,
    alignItems: 'center',
    width: '48%',
    shadowColor: '#000',
    shadowOpacity: 0.05,
    shadowRadius: 6,
    elevation: 4,
    marginBottom: 20,
  },
  cardFull: {
    backgroundColor: '#fff',
    marginTop: 10,
    padding: 20,
    borderRadius: 16,
    alignItems: 'center',
    width: '100%',
    shadowColor: '#000',
    shadowOpacity: 0.05,
    shadowRadius: 6,
    elevation: 4,
    marginBottom: 30,
  },
  cardLabel: {
    fontSize: 16,
    marginTop: 8,
    color: '#333',
    fontFamily: 'System',
  },
  cardValue: {
    fontSize: 22,
    fontWeight: 'bold',
    color: '#111',
    fontFamily: 'System',
  },
  syncButton: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#db4437',
    padding: 12,
    borderRadius: 10,
    paddingHorizontal: 20,
    marginBottom: 16,
  },
  syncButtonText: {
    color: 'white',
    fontWeight: '600',
    marginLeft: 8,
    fontSize: 16,
    fontFamily: 'System',
  },
  quote: {
    fontSize: 15,
    color: '#333',
    textAlign: 'center',
    fontStyle: 'italic',
    fontFamily: 'System',
    marginTop: 10,
  },
});
